mapper.select:
select *  from o_admin
 where ACCOUNT=#{account}
 and password=#{password}

select * from o_admin



   and account like #{newAccount}



   and name like #{newName}



   and phone = #{phone}



   and email = #{email}



   and status = #{status}



   and sex = #{sex}



   and roleid = #{roleid}

mapper.update:
update o_admin set STATUS=2
WHERE id in

    #{myid}

update o_admin set STATUS=0
where id in

  #{myid}
